static function Collection[Domain.OrderWMS] GetTopRAs()
{
    Collection[string] RAs;
	RAs.Add("Al");
	RAs.Add("Yw");
	RAs.Add("Ec");
	RAs.Add("Hw");
	RAs.Add("Fr");
	RAs.Add("Js");
	RAs.Add("Yb");
	RAs.Add("Fi");
	RAs.Add("Oo");
	RAs.Add("Bl");
	RAs.Add("Jk");

	Collection[Domain.OrderWMS] orders = Domain.OrderWMS.GetAll().Where(o => RAs.Contains(o.OrdAgencyCode));
    return orders;
}

