static function Collection[Domain.SLAStatistics] ItemLeadTimePercentageWithCriteria(
	Collection[int] supplierIds,
	Collection[int] warehouseIds,
	Collection[int] businessUnitIds,
	Collection[int] categoryIds,
	Collection[int] subCategoryIds,
	Collection[int] baseUnitIds,
	Collection[string] SKUs,
	DateTime fromDate,
	DateTime toDate
)
{
    Collection[Domain.SLAStatistics] stats;

    Collection[int] emptyIDs;
	Collection[string] emptySKUs;
    if(warehouseIds == null) {
        warehouseIds = emptyIDs;
    }
    if(SKUs == null) {
        SKUs = emptySKUs;
    }
    if(businessUnitIds == null) {
        businessUnitIds = emptyIDs;
    }
    if(categoryIds == null) {
        categoryIds = emptyIDs;
    }
    if(subCategoryIds == null) {
        subCategoryIds = emptyIDs;
    }
    if(baseUnitIds == null) {
        baseUnitIds = emptyIDs;
    }
    if(supplierIds == null) {
        supplierIds = emptyIDs;
    }

    Collection[Domain.SLALeadTime] filteredLeadTimes
		= Domain.SLALeadTime.Find(a =>
				(SKUs.Length == 0 || SKUs.Contains(a.Item.SKU))
				&&
				(businessUnitIds.Length == 0 || businessUnitIds.Contains(a.Item.BusinessUnit.Id))
				&&
				(categoryIds.Length == 0 || categoryIds.Contains(a.Item.Category.Id))
				&&
				(subCategoryIds.Length == 0 || subCategoryIds.Contains(a.Item.SubCategory.Id))
				&&
				(baseUnitIds.Length == 0 || baseUnitIds.Contains(a.Item.BaseUnit.Id))
				&&
				(supplierIds.Length == 0 || supplierIds.Contains(a.Item.Supplier.Id))
				&&
				(warehouseIds.Length == 0 || warehouseIds.Contains(a.Warehouse.Id))
				&&
				(fromDate == null || a.Month >= fromDate.Month())
				&& 
				(toDate == null || a.Month <= toDate.Month())
			);

//	DebugLib.Logger.WriteInfoLine("filteredLeadTimes: " + filteredLeadTimes.Length);

	var uniqueMonths = filteredLeadTimes.Select(a=>a.Month).Distinct().OrderBy(a=>a);

//	DebugLib.Logger.WriteInfoLine("-----> uniqueMonths: " + uniqueMonths.Length);

	foreach int i in uniqueMonths {
	    Collection[Domain.SLALeadTime] thisMonthsLTs = filteredLeadTimes.Where(a=>a.Month == i);
	    if(thisMonthsLTs.Length == 0){
	    	DebugLib.Logger.WriteInfoLine("Continue...");
	        continue;
	    }

	    decimal test = thisMonthsLTs.Sum(a=>a.MonthlyPercentage);

//	    DebugLib.Logger.WriteInfoLine("Length months " + uniqueMonths.Length.ToString());
//	    DebugLib.Logger.WriteInfoLine("test " + test.ToString());
		Domain.SLAStatistics stat;
		stat.Percentage = thisMonthsLTs.Sum(a=>a.MonthlyPercentage)/thisMonthsLTs.Length;
		stat.Month = AppLib.Globalization.GetMonthName(i);
//		DebugLib.Logger.WriteInfoLine("Month " + i.ToString());
//		DebugLib.Logger.WriteInfoLine("Chart.. " + stat.Percentage.ToString());
		stat.Threshold = 80;
		stats.Add(stat);
	}

	return stats;
}

static function Collection[Domain.SLAStatistics] ItemLeadTimePercentage(string sku) 
{

    Domain.Item item;
    Collection[Domain.SLAStatistics] stats;

    if string.IsNullOrEmpty(sku){
	    return stats;
	}
	else {
		try {
			item = Domain.Item.GetByKey(sku);
		}
		catch Exception x {

		}
	}

	foreach Domain.SLALeadTime leadTime in item.SLALeadTimes {
		Domain.SLAStatistics stat;
		stat.Item = item;
		stat.Percentage = leadTime.MonthlyPercentage;
		stat.Month = AppLib.Globalization.GetMonthName(leadTime.Month);
		stat.Threshold = 80;
		stats.Add(stat);
	}


	return stats;
}

