	Collection[int] categoryIds,
	Collection[int] subCategoryIds,
	Collection[int] baseUnitIds,
	Collection[string] SKUs,
	DateTime fromDate,
	DateTime toDate
)
{
    Collection[Domain.SLAStatistics] stats;

    Collection[int] emptyIDs;
	Collection[string] emptySKUs;
    if(warehouseIds == null) {
        warehouseIds = emptyIDs;
    }
    if(SKUs == null) {
        SKUs = emptySKUs;
    }
    if(businessUnitIds == null) {
        businessUnitIds = emptyIDs;
    }
    if(categoryIds == null) {
        categoryIds = emptyIDs;
    }
    if(subCategoryIds == null) {
        subCategoryIds = emptyIDs;
    }
    if(baseUnitIds == null) {
        baseUnitIds = emptyIDs;
    }
    if(supplierIds == null) {
        supplierIds = emptyIDs;
    }

    var filteredDeliveries
		= Domain.SLADelivery.Find(a =>
				(SKUs.Length == 0 || SKUs.Contains(a.Item.SKU))
				&&
				(businessUnitIds.Length == 0 || businessUnitIds.Contains(a.Item.BusinessUnit.Id))
				&&
				(categoryIds.Length == 0 || categoryIds.Contains(a.Item.Category.Id))
				&&
				(subCategoryIds.Length == 0 || subCategoryIds.Contains(a.Item.SubCategory.Id))
				&&
				(baseUnitIds.Length == 0 || baseUnitIds.Contains(a.Item.BaseUnit.Id))
				&&
				(supplierIds.Length == 0 || supplierIds.Contains(a.Item.Supplier.Id))
				&&
				(warehouseIds.Length == 0 || warehouseIds.Contains(a.Warehouse.Id))
				&&
				(fromDate == null || a.Month >= fromDate.Month())
				&& 
				(toDate == null || a.Month <= toDate.Month())
			);

	var uniqueMonths = filteredDeliveries.Select(a=>a.Month).Distinct().OrderBy(a=>a);


	if SKUs.Contains("4047706") && SKUs.Length == 1{

		DebugLib.Logger.WriteInfoLine("Months: " + uniqueMonths.Length);

	}

	foreach int i in uniqueMonths {
	    Collection[Domain.SLADelivery] thisMonthsDeliveries = filteredDeliveries.Where(a=>a.Month == i);
	    if(thisMonthsDeliveries.Length == 0){
	        continue;
	    }

	    if SKUs.Contains("4047706") && SKUs.Length == 1{

		DebugLib.Logger.WriteInfoLine("thisMonthsDeliveries: " + thisMonthsDeliveries.Length);

	}

		Domain.SLAStatistics stat;
		stat.Percentage = thisMonthsDeliveries.Sum(a=>a.Percentage)/thisMonthsDeliveries.Length;
		stat.Percentage =  CommonLib.Math.Round(stat.Percentage,2);
		stat.Month = AppLib.Globalization.GetMonthName(i);
		stat.Threshold = 80;
		stats.Add(stat);
	}

	return stats;
}

function bool Exists()
{
	if Domain.Item.Find(a=> a.SKU == this.SKU).Length > 0 {
	    return true;
	}
	return false;
}

static function int GetUnitsOfStock(Domain.StockOnHand stock) 
{
    //WHY AM I HERE ?????
    var lastStock = Domain.StockOnHand.Find(a=> a.StockOnHandDate.Date < stock.StockOnHandDate.Date).OrderByDescending(b=>b.StockOnHandDate.Date).First();

    if(lastStock == null){
    	return 0;
    }
	int LastPastStock = lastStock.Units;

	var salesforecast = Domain.SalesForecastDetails.Find(s=> s.ForecastDate.Date == stock.StockOnHandDate.Date 
	&& stock.Warehouse == s.Warehouse && s.Item == stock.Item).First();

    if(salesforecast == null){
    	return 0;
    }

	int salesForecastUnits = salesforecast.Units;

	return (LastPastStock - salesForecastUnits); //+ Received Stock
}

static function Collection[Domain.SLAStatistics] CalculateOrderForecastAccuracy(
	Collection[int] supplierIds,
	Collection[int] warehouseIds,
	Collection[int] businessUnitIds,
	Collection[int] categoryIds,
	Collection[int] subCategoryIds,
	Collection[int] baseUnitIds,
	Collection[string] SKUs,
	DateTime fromDate,
	DateTime toDate) 
{

	Collection[Domain.SLAStatistics] stats;
    Collection[int] emptyIDs;
	Collection[string] emptySKUs;
    if(warehouseIds == null) {
        warehouseIds = emptyIDs;
    }
    if(SKUs == null) {
        SKUs = emptySKUs;
    }
    if(businessUnitIds == null) {
        businessUnitIds = emptyIDs;
    }
    if(categoryIds == null) {
        categoryIds = emptyIDs;
    }
    if(subCategoryIds == null) {
        subCategoryIds = emptyIDs;
    }
    if(baseUnitIds == null) {
        baseUnitIds = emptyIDs;
    }
    if(supplierIds == null) {
        supplierIds = emptyIDs;
    }

    var filteredPlatforms
		= Domain.Platform.Find(a =>
				(SKUs.Length == 0 || SKUs.Contains(a.Item.SKU))
				&&
				(businessUnitIds.Length == 0 || businessUnitIds.Contains(a.Item.BusinessUnit.Id))
				&&
				(categoryIds.Length == 0 || categoryIds.Contains(a.Item.Category.Id))
				&&
				(subCategoryIds.Length == 0 || subCategoryIds.Contains(a.Item.SubCategory.Id))
				&&
				(baseUnitIds.Length == 0 || baseUnitIds.Contains(a.Item.BaseUnit.Id))
				&&
				(supplierIds.Length == 0 || supplierIds.Contains(a.Item.Supplier.Id))
				&&
				(warehouseIds.Length == 0 || warehouseIds.Contains(a.Warehouse.Id))
				&&
				(fromDate == null || int.Parse(a.Month) >= fromDate.Month())
				&& 
				(toDate == null || int.Parse(a.Month) <= toDate.Month())
			);


	var uniqueMonths = filteredPlatforms.Select(a=>int.Parse(a.Month)).Distinct().OrderBy(a=>a);

	foreach int i in uniqueMonths 
	{
	    Collection[Domain.Platform] thisMonthsPlatforms = filteredPlatforms.Where(a=> int.Parse(a.Month) == i);

	    if (thisMonthsPlatforms.Length == 0)
	    {
	        continue;
	    }

		Domain.SLAStatistics stat;
		stat.Percentage = thisMonthsPlatforms.Sum(a=>a.percentage) / thisMonthsPlatforms.Length;
		stat.Percentage =  CommonLib.Math.Round(stat.Percentage,2) / 100;
		stat.Month = AppLib.Globalization.GetMonthName(i);
		stat.Threshold = 80;
		stats.Add(stat);
	}

	return stats;

}

static function Collection[Domain.Platform] CalculatePlatform()
{
	Collection[Domain.Platform] leadTimes;

	foreach Domain.Item: item in Domain.Item.GetAll().Take(15) {
		Domain.Platform leadTime;
		leadTime.RealOrderedQuantities = int.GetRandom((item.AvgDailyDemand + item.AvgDailyDemand +item.AvgDailyDemand )as int,100,2000) as decimal;
		leadTime.ForecastOrders = int.GetRandom(CommonLib.Math.Abs((item.AvgDailyDemand + item.AvgDailyDemand  - item.AvgDailyDemand) as int),100,2000) as decimal;



		leadTime.Item = item;
		leadTime.percentage = CommonLib.Math.Abs((leadTime.ForecastOrders) / leadTime.RealOrderedQuantities)  * 100;
		leadTime.ItemDescription = item.Description.Substring(0, 15);

		if(leadTime.percentage > 0.5){
		    decimal perc = int.GetRandom((item.AvgDailyDemand + item.AvgDailyDemand ) as int, 1,3);
			leadTime.percentage = ((perc * item.AvgDailyDemand ) / 100 ) +  leadTime.percentage - 100 ; 
		}

		if(leadTime.percentage > 15){
		    leadTime.percentage = leadTime.percentage - 20;
		}
		if(leadTime.percentage < 0){
		    leadTime.percentage = leadTime.percentage + 10;
		}


		leadTimes.Add(leadTime);
		leadTime.Save();
	}
	return leadTimes;
}
